var mongoose = require('mongoose');
var ObjectId = mongoose.Schema.Types.ObjectId;
require('../models/index.js');
var async = require('async');

/**
 * Created by Roman on 04.04.2015.
 */

var userSchema = mongoose.Schema({
    imageSrc: {
        type: String,
        default: 'data:image/png;base64,'
    },
    login: {type: String, default: ''},
    email: {type: String, default: ''},
    pass: {type: String, default: ''},
    credentials: {
        refresh_token: {type: String, default: ''},
        access_token: {type: String, default: ''}
    },
    profile: {type: Number, ref: "Profile"},
    lastAccess: {type: Date},
    kanbanSettings: {
        opportunities: {
            countPerPage: {type: Number, default: 10},
            foldWorkflows: [{type: String, default: ''}]
        },
        applications: {
            countPerPage: {type: Number, default: 10},
            foldWorkflows: [{type: String, default: ''}]
        },
        tasks: {
            countPerPage: {type: Number, default: 10},
            foldWorkflows: [{type: String, default: ''}]
        }
    },
    savedFilters: [{type: ObjectId, ref: 'savedFilters', default: []}],
    RelatedEmployee: {type: ObjectId, ref: 'Employees', default: null}
}, {collection: 'Users'});

mongoose.model('UserOld', userSchema);

if (!mongoose.Schemas) {
    mongoose.Schemas = {};
}

mongoose.Schemas['UserOld'] = userSchema;


var uSchema = mongoose.Schemas['User'];
var UserOldSchema = mongoose.Schemas['UserOld'];

var dbObject = mongoose.createConnection('localhost', 'weTrack');
dbObject.on('error', console.error.bind(console, 'connection error:'));
dbObject.once('open', function callback() {
    console.log("Connection to weTrack is success");
});

var User = dbObject.model("Project", uSchema);
var UserOld = dbObject.model("ProjectNew", UserOldSchema);

var query = UserOld.find().lean();

query.exec(function (error, _res) {
    if (error) {
        return console.dir(error);
    }

    async.eachLimit(_res, 100, function (user, callback) {
        var objectToSave = {};
        var newSavedFilters = [];
        var obj = {};


        if (user) {
            user.savedFilters.forEach(function(filter){
                obj = {
                    _id: filter,
                    byDefault: ""
                };

                newSavedFilters.push(obj);
            });

            objectToSave = {
                savedFilters: newSavedFilters
            }
        }

        User.update({_id: user._id}, objectToSave, callback);
    }, function (err) {
        if (err) {
            return console.dir(err);
        }

        console.dir('Good');
    })
});